mapper.select:
SELECT * FROM (
	SELECT rownum r_num,a.* FROM (SELECT * FROM bbs_t
					WHERE status = 0 AND bname = #{bname}
					ORDER BY b_idx DESC
	) a
) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE bbs_t
SET status = 1
WHERE b_idx = ${no}

UPDATE bbs_t
SET hit = hit+1
WHERE b_idx = #{no}

UPDATE bbs_t
SET
	subject = #{subject},
	content = #{content}

		,file_name = #{file_name}
		,ori_name = #{ori_name}

WHERE b_idx = #{b_idx} AND pwd = #{pwd}
